    box-shadow: 0px 0px 50px 10px rgba(40, 40, 40, 0.1);
  }
  .projects-container .left-project .back,
.projects-container .right-project .back {
    transform: rotateY(180deg) translateZ(var(--z-offset)) scale(0.79);
    transition: all 0.6s ease-in-out;
  }
  .projects-container .left-project .back::before,
.projects-container .right-project .back::before {
    transform: translateZ(var(--z-offset)) scale(1.27);
  }
  .projects-container .left-project {
    max-width: 384px;
    max-height: 384px;
    transform: translateX(calc(min(384px, var(--side-box-width-height)) * -1)) translateZ(-100px);
  }
  .projects-container .right-project {
    max-width: 384px;
    max-height: 384px;
    transform: translateX(calc(min(384px, var(--side-box-width-height)))) translateZ(-100px);
  }
