    grid-template-columns: 1fr 1fr 1fr;
    width: 0.22em;
    height: 0.1em;
  }
  .projects-container .tool .tool-logo .logo {
    height: 0.04em;
  }
  .projects-container .tool .tool-name {
    font-size: 0.012em;
  }
  .projects-container .back .btn-default {
