sources.sass\\base\\_root.scss:
@use '../abstracts' as *;

:root {
	--header-height: 64px;
	--hero-height: 880px;
	--hero-width: 3000px;
	--primary-font: 'lemon-milk';
	--secondary-font: Montserrat, sans-serif;
	--sections-padding-block: 90px;
	--side-box-width-height: 80vw;
	--center-box-width-height: 80vw;
	--container-height: fit-conetent;
	--z-offset: 20vw;

	@each $size, $value in $breakpoints {
		--breakpoint-#{$size}: #{$value};
	}

	@each $color, $value in $colors {
		--#{$color}: #{$value};
	}

	@each $color, $value in $grays {
		--gray-#{$color}: #{$value};
	}

	@each $color, $value in $theme-colors {
		--#{$color}: #{$value};
	}
}

@media (min-width: 576px) {
	:root {
		--z-offset: 7vw;
	}
}

sources.sass\\base\\_reset.scss:
/* Box sizing rules */
*,
*::before,
*::after {
	box-sizing: border-box;
}

/* Remove default margin */
body,
h1,
h2,
h3,
h4,
p,
figure,
blockquote,
dl,
dd {
	margin: 0;
}


/* Remove list styles on ul, ol elements with a list role, which suggests default styling will be removed */
ul[role='list'],
ol[role='list'] {
	list-style: none;
	padding: 0;
}

/* Set core root defaults */
html:focus-within {
	scroll-behavior: smooth;
}

/* Set core body defaults */
body {
	min-height: 100vh;
	overflow-x: hidden;
	width: 100vw;
	text-rendering: optimizeSpeed;
	line-height: 1.5;
}

/* A elements that don't have a class get default styles */
a:not([class]) {
	text-decoration-skip-ink: auto;
}

/* Make images easier to work with */
img,
picture {
	// max-width: 100%;
	display: block;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
	font: inherit;
}

/* Remove all animations, transitions and smooth scroll for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
	html:focus-within {
		scroll-behavior: auto;
	}

	*,
	*::before,
	*::after {
		animation-duration: 0.01ms !important;
		animation-iteration-count: 1 !important;
		transition-duration: 0.01ms !important;
		scroll-behavior: auto !important;
	}
}

