	margin-inline: 1rem;
}

h2,
.h2 {
	font-family: var(--primary-font);
	position: relative;
	text-align: center;
	letter-spacing: 2px;
	font-size: var(--section-title-font-size);
	line-height: 14px;
	padding: 0 0 15px 0;
	color: var(--dark);
	font-size: 30px;

	&::before {
		width: 40px;
		height: 2px;
		content: '';
		position: absolute;
		top: 40px;
		left: 50%;
		background-color: #efa24b;
		transform: translateX(-50%);
	}
}

h3,
.h3 {
	margin-inline: auto;
	font-size: 20px;
	font-weight: 600;
	line-height: 1.22;
	letter-spacing: 1px;
	font-family: var(--secondary-font);
	margin-top: 20px;
	text-align: center;
	color: var(--dark);
}

h4,
.h4 {
	margin-inline: auto;
	font-weight: 600;
	font-size: 16px;
	line-height: 1.22;
	letter-spacing: 1px;
	font-family: var(--secondary-font);
	margin-top: 20px;
	margin-bottom: 5px;
	text-align: center;
	color: var(--dark);
}

p,
.p {
	margin-top: 18px;
	font-family: var(--secondary-font);
	font-size: 14px;
	line-height: 1.73;
	letter-spacing: 0.5px;
	color: #626881;
	text-align: center;
}

@media (min-width: 768px) {
	h1,
	.h1 {
		font-size: 80px;
	}
}

sources.sass\\base\\_animations.scss:
// navbar
.fade {
	opacity: 1 !important;
	pointer-events: auto !important;
}

.stop-scrolling {
	height: 100%;
	overflow: hidden;
}

.animate-top-line {
	background-color: #999 !important;
	transform: translateY(-4px) rotate(45deg);
}

.animate-bottom-line {
	background-color: #999 !important;
	transform: translateY(4px) rotate(-45deg);
}

// planets
@keyframes rotate-planet {
	0% {
		transform: translateX(0);
	}
	100% {
		transform: translateX(-50%);
	}
}

@keyframes rotate-planet-reverse {
	0% {
		transform: translateX(-50%);
	}
	100% {
		transform: translateX(0);
	}
}

// Contact slide out window
.show-bg {
	opacity: 0.5 !important;
	pointer-events: all !important;
}

.slide-in {
	transform: translateX(0);
	pointer-events: all !important;
	right: auto !important;
	opacity: 1 !important;
}

// Stars
@keyframes shooting-star {
	0% {
		transform: translateX(0);
	}
	2% {
		transform: translateX(-2000px);
	}
	4% {
		transform: translateX(-2000px);
		opacity: 0;
	}
	100% {
		transform: translateX(-2000px);
		opacity: 0;
	}
}

@keyframes flicker {
	0% {
		opacity: 1;
	}
	50% {
		opacity: 0;
	}
	100% {
		opacity: 1;
	}
}

// Skills icons animation
@keyframes terminal {
	5% {
		opacity: 1;
	}
	6% {
		opacity: 0;
	}
	7% {
		opacity: 1;
	}
	8% {
		opacity: 0;
	}
	9% {
		opacity: 1;
	}
	10% {
		opacity: 0;
	}
	11% {
		opacity: 1;
	}
	12% {
		opacity: 0;
	}
	13% {
		opacity: 1;
	}
}

@keyframes brush {
	18% {
		transform: scale(1) translate(0);
	}
	20% {
		transform: scale(1.2) rotate(10deg);
	}
	21% {
		transform: scale(1.2) rotate(10deg);
	}
	22% {
		transform: scale(1.2) rotate(40deg);
	}
	23% {
		transform: scale(1.2) rotate(-15deg);
	}
	24% {
		transform: scale(1.2) rotate(10deg);
	}
	25% {
		transform: scale(1) rotate(0);
	}
}

@keyframes user {
	30% {
		transform: scale(1) translate(0);
	}
	32% {
		transform: scale(1.2) rotate(30deg);
	}
	34% {
		transform: scale(1.2) rotate(-400deg);
	}
	36% {
		transform: scale(1.2) rotate(30deg);
	}
	38% {
		transform: scale(1) rotate(0);
	}
}

@keyframes rotate-planet-icon {
	43% {
		transform: translateX(0);
	}
	46% {
		transform: translateX(-50%);
	}
	49% {
		transform: translateX(0);
	}
}

@keyframes flash {
	54% {
		transform: scale(1);
		opacity: 0;
	}
	55% {
		transform: scale(1.2);
		opacity: 1;
	}
	56% {
		transform: scale(1);
		opacity: 0;
	}
	57% {
		transform: scale(1.2);
		opacity: 1;
	}
	58% {
		transform: scale(1);
		opacity: 0;
	}
	59% {
		transform: scale(1.2);
		opacity: 1;
	}
	60% {
		transform: scale(1);
		opacity: 0;
	}
}

@keyframes bulb-on {
	65% {
		opacity: 0;
	}
	66% {
		opacity: 1;
	}
	71% {
		opacity: 1;
	}
	72% {
		opacity: 0;
	}
}

// Projects
.rotate {
	transform: rotateY(180deg);
}

