sources.sass\\sections\\_contact.scss:
@use 'abstracts' as *;

section.home-contact-container {
	// @include center-children-hor;
	display: flex;
	position: fixed;
	z-index: 6;
	width: 100vw;
	height: 100vh;
	pointer-events: none;
	overflow-y: scroll;
	overflow-x: hidden;
}

.contact-bg {
	@include width-height-of(100%);
	position: fixed;
	left: 0;
	background-color: black;
	opacity: 0;
	pointer-events: none;
	transition: opacity 0.5s ease-in-out;
}

.contact-container {
	@include center-children-vir;
	display: absolute;
	margin: auto;
	width: 100%;
	height: 100%;
	min-height: 820px;
	background-color: white;
	box-shadow: 0px 0px 40px 10px rgba(0, 0, 0, 0.2);
	transition: all 0.5s ease-in-out;

	opacity: 0;

	&::before {
		display: none;
		position: absolute;
		content: '';
		width: 88%;
		height: 95%;
		border-radius: 40px;
		border: solid 2px rgb(240, 240, 240);
		z-index: -1;
	}
}

.hide-contact {
	transform: translateX(calc(50vw + 350px));
}

.close-contact-button {
	@include center-children-hor;
	background-color: transparent;
	cursor: pointer;
	position: absolute;
	width: 30px;
	height: 30px;
	top: 5%;
	right: 10%;
	border: none;
	color: var(--gray-400);
	font-size: 30px;

	.lines-wrapper {
		height: 27px;
	}
	.top-line,
	.bottom-line {
		background-color: var(--gray-400);
		height: 5px;
	}

	.top-line {
		transform: rotate(45deg);
	}

	.bottom-line {
		transform: rotate(-45deg);
	}
}

form {
	width: 80%;
	@include center-children-vir;

	h2::before {
		background-color: var(--pink);
	}

	p {
		font-family: var(--secondary-font);
		color: var(--gray);
		font-size: 15px;
		margin-bottom: 0;
	}

	.g-recaptcha {
		margin-top: 35px;
	}
}

.send-button {
	position: relative;
	i {
		font-size: 20px;
		position: absolute;
		left: 50px;
	}
}

.blobs {
	position: absolute;
	height: 920px;
	z-index: 1;
	pointer-events: none;
	transform: translate(-30px, -10px);
	opacity: 0;
	transition: all 0.3s ease;
}

.blob-shadows {
	position: absolute;
	height: 990px;
	z-index: 1;
	pointer-events: none;
	transform: translate(-15px, 10px);
	z-index: -1;
	filter: blur(5px);
	opacity: 0;
	transition: all 0.3s ease;
}

@media (min-width: 576px) {
	.contact-container {
		border-radius: 50px;
		width: 640px;
		height: 820px;

		&::before {
			display: block;
			height: 90%;
		}
	}

	form {
		width: 65%;
	}
	.close-contact-button {
		top: 8%;
		right: 16%;
	}

	.blob-shadows {
		opacity: 0.5;
	}
	.blobs {
		opacity: 1;
	}
}

